import React from 'react';
import ReactDOM from 'react-dom';
import FittedImage from 'react-fitted-image';
import logo from './logo.svg';
import './style.css';
import mole from './mole.jpg'

let squares = Array(9)

function Mole() {
  return(
    <div class="square">
      <FittedImage fit="Auto" src={mole}/>
    </div>
  );
}

function Square(props) {
  return (
    <div class="square" id={props.id}>
      <FittedImage fit="Auto" src={props.img}/>
    </div>
  );
}

class Board extends React.Component {
  randomSquare() {
    squares.forEach(className => {
      className.classList.remove({mole})
    })
  }

  render () {
    return (
      <div class="grid">
          <Square id = "1" img = {mole}/>
          <Square id = "2" />
          <Square id = "3" />
          <Square id = "4" />
          <Square id = "5" />
          <Square id = "6" />
          <Square id = "7" />
          <Square id = "8" />
          <Square id = "9" />
          {this.randomSquare()}
      </div>
    );
  }
}

class Game extends React.Component {
  addMole(id) {
    return (
      <div class="square" id={id}>
        <Mole/>
      </div>
    );
  }

  render () {
    return (
      <div>

        <h1>Whack-A-Mole</h1>

        <h4>Score:</h4>
        <h4 id="score">0</h4>

        <h4>Seconds left:</h4>
        <h4 id="time-left">60</h4>

        <Board/>

      </div>
    );
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
