import React from 'react';
import ReactDOM from 'react-dom';
import Timer from 'react-compound-timer'
import HashMap from 'hashmap';
import './style.css';
import mole from './mole.jpg'

/**
 * Represents square component
 * @param {*} props 
 */
class Square extends React.Component {

  render () {
    return (
      <button className={this.props.className} 
        id={this.props.id} 
        onClick={() => this.props.onClick()}>
      </button>
    );
  }
}

class Board extends React.Component {

  //onClick={() => this.props.handleClick(i, this.props.squares[i])}
  renderSquare(i) {
    return (
      <Square
        id={i}
        className={this.props.squares[i]}
        onClick={() => this.props.scoreCounter(i)}
      />
    );
  }

  render () {
    return (
      <div className="grid">
        {this.renderSquare(0)}
        {this.renderSquare(1)}
        {this.renderSquare(2)}

        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}

        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill("square"),
      score: 0,
      timeLeft: 60,
    };
  }

  hitPosition = -1;

  randomSquare()  {
    const sqs = Array(9).fill("square");
    let randomPosition = Math.floor(Math.random() * 8)
    console.log("Rand position = " + randomPosition)
    sqs[randomPosition] = "mole"
    this.hitPosition = randomPosition
    this.setState({squares: sqs});
  }

  handleClick(i, c) {
    const sqs = this.state.squares.slice();
    if (c === "square") {
      sqs[i] = "mole"
    } else {
      sqs[i] = "square"
    }
    console.log(c + " at index " + i)
    console.log(sqs[i])
    this.setState({squares: sqs});
  }

  scoreCounter(i) {

    if (i === this.hitPosition) {
      this.setState({score: this.state.score + 1})
    }
  }

  /*setInterval(countDown() {
    timeLeft.textContent = currentTime

    if(currentTime === 0) {
        clearInterval(timerId)
        alert('GAME OVER! Your final score is ' + result)
    }
}, 1000)*/

  //interval = setInterval(this.countDown(), 1000)
  /*countDown() {
    let timer = setInterval(() => this.setState({
      timeLeft: this.state.timeLeft
    }), 1000)
    /*this.setState({timeLeft: this.state.timeLeft - 1})

    if(this.state.timeLeft === 0) {
        clearInterval(this.interval)
        alert('GAME OVER! Your final score is ' + this.state.score)
    }
  }*/

  //<Board squares= {this.state.squares} handleClick= {(i,c) => this.handleClick(i,c)}/>
  render () {
    return (
      <div className="center">
        <button onClick={() => this.countDown()}>start</button>
        <button onClick={() => this.randomSquare()}>{this.hitPosition}</button>
        <h1>Whack-A-Mole</h1>

        <h4 id="score">Score: {this.state.score}</h4>

        <h4 id="time-left">Seconds left: {this.state.timeLeft}</h4>
        <Timer initialTime={this.state.timeLeft * 1000} 
          direction="backward"
          checkpoints={[{
            time: 0,
            callback: () => alert('Game Over! Your final score is ' + this.state.score),
          }]}>
          {() => (
            <React.Fragment>
                <Timer.Seconds /> seconds
            </React.Fragment>
          )}
        </Timer>
        <Board squares= {this.state.squares} scoreCounter= {(i) => this.scoreCounter(i)}/>

      </div>
    );
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
