import React from 'react';
import ReactDOM from 'react-dom';
import HashMap from 'hashmap';
import './style.css';
import mole from './mole.jpg'

/**
 * Represents a single square component
 * @param {*} props 
 */
class Square extends React.Component {

  render () {
    return (
      <button className={this.props.className} 
        id={this.props.id} 
        onClick={() => this.props.onClick()}>
      </button>
    );
  }
}

/**
 * 
 */
class Board extends React.Component {

  renderSquare(i) {
    return (
      <Square
        id={i}
        className={this.props.squares[i]}
        onClick={() => this.props.scoreCounter(i)}
      />
    );
  }

  render () {
    return (
      <div className="grid">
        {this.renderSquare(0)}
        {this.renderSquare(1)}
        {this.renderSquare(2)}

        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}

        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill("square"),
      score: 0,
      timeLeft: 10,
    };
  }

  hitPosition = -1;

  randomSquare()  {
    const sqs = Array(9).fill("square");
    let randomPosition = Math.floor(Math.random() * 8)
    console.log("Rand position = " + randomPosition)
    sqs[randomPosition] = "mole"
    this.hitPosition = randomPosition
    this.setState({squares: sqs});
  }

  handleClick(i, c) {
    const sqs = this.state.squares.slice();
    if (c === "square") {
      sqs[i] = "mole"
    } else {
      sqs[i] = "square"
    }
    console.log(c + " at index " + i)
    console.log(sqs[i])
    this.setState({squares: sqs});
  }

  scoreCounter(i) {

    if (i === this.hitPosition) {
      this.setState({score: this.state.score + 1})
    }
  }

  startTimer () {
    const startCount = this.state.timeLeft
    this.setState({
      timeLeft: startCount
    })
    this.myInterval = setInterval(() => {
      if (this.state.timeLeft >= 0) {
        this.setState({
          timeLeft: this.state.timeLeft - 1
        })
        this.randomSquare()
      } else {
        clearInterval(this.myInterval)
        this.setState({squares: Array(9).fill("square")})
        alert('GAME OVER! Your final score is ' + this.state.score)
      }
    }, 1000)
  }

  stopTimer () {
    clearInterval(this.myInterval)
  }

  render () {
    return (
      <div className="center">
        <button onClick={() => this.stopTimer()}>stop</button>
        <button onClick={() => this.startTimer()}>start</button>
        <button onClick={() => this.randomSquare()}>{this.hitPosition}</button>
        <h1>Whack-A-Mole</h1>

        <h4 id="score">Score: {this.state.score}</h4>

        <h4 id="time-left">Seconds left: {this.state.timeLeft}</h4>

        <Board squares= {this.state.squares} scoreCounter= {(i) => this.scoreCounter(i)}/>

      </div>
    );
  }

  componentDidMount () {

  }

  componentWillUnmount () {
    clearInterval(this.myInterval)
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
