import React from 'react';
import ReactDOM from 'react-dom';
import HashMap from 'hashmap';
import './style.css';
import mole from './mole.jpg'

/**
 * Represents square component
 * @param {*} props 
 */
class Square extends React.Component {

  render () {
    return (
      <button className={this.props.className} 
        id={this.props.id} 
        onClick={() => this.props.onClick()}>
      </button>
    );
  }
}

class Board extends React.Component {

  renderSquare(i) {
    return (
      <Square
        id={i}
        className={this.props.squares[i]}
        onClick={() => this.props.handleClick(i, this.props.squares[i])}
      />
    );
  }

  render () {
    return (
      <div className="grid">
        {this.renderSquare(0)}
        {this.renderSquare(1)}
        {this.renderSquare(2)}

        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}

        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
      </div>
    );
  }
}

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill("square"),
      score: 0,
    };
  }

  hitPosition = -1;

  randomSquare()  {
    const sqs = Array(9).fill("square");
    let randomPosition = Math.floor(Math.random() * 8)
    console.log("Rand position = " + randomPosition)
    sqs[randomPosition] = "mole"
    this.hitPosition = randomPosition
    this.setState({squares: sqs});
  }

  handleClick(i, c) {
    const sqs = this.state.squares.slice();
    if (c === "square") {
      sqs[i] = "mole"
    } else {
      sqs[i] = "square"
    }
    console.log(c + " at index " + i)
    console.log(sqs[i])
    this.setState({squares: sqs});
  }

  hClick(i, c) {
    if (i === this.hitPosition) {
      this.setState({score: this.state.score + 1})
    } else {
      this.setState({score: this.state.score - 1})
    }
    //this.setState({squares: sqs})
  }

  render () {
    return (
      <div>
        <button className="square" onClick={() => this.randomSquare()}>{this.hitPosition}</button>
        <a> Score: {this.state.score}</a>
        <h1>Whack-A-Mole</h1>

        <h4>Score:</h4>
        <h4 id="score">0</h4>

        <h4>Seconds left:</h4>
        <h4 id="time-left">60</h4>

        <Board squares= {this.state.squares} handleClick= {(i,c) => this.handleClick(i,c)}/>

      </div>
    );
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
