import React from 'react';
import ReactDOM from 'react-dom';
import FittedImage from 'react-fitted-image';
import HashMap from 'hashmap'
import logo from './logo.svg';
import './style.css';
import mole from './mole.jpg'

/**
 * TODO:- Change to class and make img a state
 * @param {*} props 
 */
class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: props.id,
      className: props.className
    }
  }

  render () {
    return (
      <div class={this.state.className} id={this.state.id} onClick={() => this.props.onClick()}>
      </div>
    );
  }
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: Array(9).fill("square"),
    };
  }

  handleClick(i, c) {
    const squares = this.state.squares.slice();
    if (c == "square") {
      squares[i] = "mole"
    } else {
      squares[i] = "square"
    }
    this.setState({squares: squares});
  }

  renderSquare(i, c) {
    return (
      <Square
        id={i}
        className={this.state.squares[i]}
        onClick={() => this.handleClick(i, c)}
      />
    );
  }

  render () {
    return (
      <div class="grid">
        {this.renderSquare(0)}
        {this.renderSquare(1)}
        {this.renderSquare(2)}

        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}

        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
      </div>
    );
  }
}

class Game extends React.Component {

  render () {
    return (
      <div>
        <div class="square"/>
        <h1>Whack-A-Mole</h1>

        <h4>Score:</h4>
        <h4 id="score">0</h4>

        <h4>Seconds left:</h4>
        <h4 id="time-left">60</h4>

        <Board/>

      </div>
    );
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
