import React from 'react';
import ReactDOM from 'react-dom';
import FittedImage from 'react-fitted-image';
import HashMap from 'hashmap'
import logo from './logo.svg';
import './style.css';
import mole from './mole.jpg'

function Mole() {
  return(
    <div class="square">
      <FittedImage fit="Auto" src={mole}/>
    </div>
  );
}

/**
 * TODO:- Change to class and make img a state
 * @param {*} props 
 */
class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: props.id,
      className: props.className
    }
  }

  render () {
    return (
      <div class={this.state.className} id={this.state.id} onClick={() => this.props.onClick()}>
      </div>
    );
  }
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      squares: new HashMap(),
    };
  }

  handleClick(i, c) {
    //const squares = this.state.squares.slice();
    let squares = this.state.squares
    squares.set(i, c);
    this.setState({squares: squares});
  }

  renderSquare(i, c) {
    return (
      <Square
        id={i}
        className={c}
        onClick={() => this.handleClick(i, c)}
      />
    );
  }

  render () {
    return (
      <div class="grid">
        {this.renderSquare(0, "square")}
        {this.renderSquare(1, "square")}
        {this.renderSquare(2, "square")}

        {this.renderSquare(3, "square")}
        {this.renderSquare(4, "square")}
        {this.renderSquare(5, "square")}

        {this.renderSquare(6, "square")}
        {this.renderSquare(7, "square")}
        {this.renderSquare(8, "square")}
      </div>
    );
  }
}

class Game extends React.Component {
  addMole(id) {
    return (
      <div class="square" id={id}>
        <Mole/>
      </div>
    );
  }

  render () {
    return (
      <div>
        <div class="square"/>
        <h1>Whack-A-Mole</h1>

        <h4>Score:</h4>
        <h4 id="score">0</h4>

        <h4>Seconds left:</h4>
        <h4 id="time-left">60</h4>

        <Board/>

      </div>
    );
  }
}

ReactDOM.render(<Game />, document.getElementById("root"));

export default Game;
